export type LanguageCode = typeof availableLanguages[number]['code'];

// Dynamic import function for lazy loading translations
export async function loadTranslation(code: LanguageCode): Promise<Translation> {
  const module = await import(`./${code}.json`);
  return module.default || module;
}

// Get language info by code
export function getLanguageInfo(code: string) {
  return availableLanguages.find(lang => lang.code === code);
}

// Check if a language uses RTL
export function isRTL(code: string): boolean {
  const lang = getLanguageInfo(code);
  return (lang as { rtl?: boolean } | undefined)?.rtl ?? false;
}
